do local function run(msg, matches) local Arash = 212990316 local Nova = 220173380 local if msg.action.type == "chat_del_user" and msg.action.user.id == tonumber(Arash) then chat_add_user("chat#id"..msg.to.id, 'user#id'..Arash, ok_cb, false) elseif msg.action.type == "chat_del_user" and msg.action.user.id == tonumber(Nova) then chat_add_user("chat#id"..msg.to.id, 'user#id'..Nova, ok_cb, false) elseif end end return { patterns = { "^!!tgservice (.+)$", }, run = run } end
